mapper.update:
UPDATE booking
SET booking_status = 2
WHERE user_id=#{userId} and book_isbn=#{bookIsbn}

UPDATE user
SET user_pwd = #{newPassword}
WHERE user_id=#{userId} and user_pwd= #{oldPassword}

UPDATE book
SET book_status=1
WHERE book_id=#{bookId}

UPDATE borrow
SET borrow_return_time = NOW()
WHERE borrow_id=#{borrowId}

mapper.select:
SELECT book_isbn
FROM book
WHERE book_id = #{bookId}

mapper.update:
UPDATE booking
SET booking_status=1
WHERE booking_id=#{bookingId}

mapper.insert:
UPDATE borrow
SET borrow_reborrow_time=NOW(),borrow_keep_time=borrow_keep_time+#{time}
WHERE borrow_id=#{borrowId} AND borrow_return_time IS NULL

mapper.select:
SELECT borrow_id,borrow.book_id,book_name,borrow_reborrow_time,borrow_borrow_time,borrow_return_time
FROM borrow LEFT JOIN book ON book.book_id = borrow.book_id
WHERE borrow_id=#{borrowId}

SELECT count(*)
FROM recommendation_form
WHERE book_isbn=#{book_isbn}
